import React, { useState, useEffect } from "react";
import axios from "axios";
import { XMLParser } from "fast-xml-parser";

const Weather = () => {
  const [forecastData, setForecastData] = useState({});
  const [locationName, setLocationName] = useState("Nykyinen sijainti haetaan...");
  const [searchLocation, setSearchLocation] = useState("");
  const [latitude, setLatitude] = useState(null);
  const [longitude, setLongitude] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [moonPhase, setMoonPhase] = useState("");
  
  const weatherDescriptions = {
    "1": "Aurinkoista ‚òÄÔ∏è",
    "2": "Puolipilvist√§ ‚õÖ",
    "3": "Pilvist√§ ‚òÅÔ∏è",
    "21": "Heikko sadekuuro üå¶",
    "22": "Sadekuuro üåß",
    "23": "Voimakas sadekuuro ‚õà",
    "31": "Heikko vesisade üå¶",
    "32": "Vesisade üåß",
    "33": "Voimakas vesisade üåßüí¶",
    "41": "Heikko lumisade ‚ùÑÔ∏è",
    "42": "Lumisade üå®",
    "43": "Voimakas lumisade üå®‚ùÑÔ∏è"
  };

  useEffect(() => {
    getUserLocation();
    calculateMoonPhase();
  }, []);
  
 
  useEffect(() => {
    if (latitude !== null && longitude !== null) {
      console.log("üîÑ Koordinaatit p√§ivittyiv√§t:", latitude, longitude);
      fetchWeatherData(latitude, longitude);
    }
  }, [latitude, longitude]);
  const reverseGeocode = async (lat, lon) => {
  
try {
    const res = await axios.get(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`
    );
    const address = res.data.address;
    const location =
      address.village ||
      address.town ||
      address.city ||
      address.municipality ||
      "Tuntematon paikka";

    console.log("üìç ReverseGeocode p√§ivitt√§√§ paikan:", location);
    setLocationName(location);
  } catch (error) {
    console.error("‚ùå Paikan haku ep√§onnistui:", error);
    setLocationName("Paikan haku ep√§onnistui");
  }
};
  const [tempLocationName, setTempLocationName] = useState("");

  const searchForLocation = async () => {
    console.log("üîç Haetaan paikka:", searchLocation); // TULOSTAA HAKUSANAN

  try {
    const res = await axios.get(
      `https://nominatim.openstreetmap.org/search?format=json&q=${searchLocation}`
    );
    console.log("üì° API-vastaus:", res.data); // TULOSTAA NOMINATIM API -VASTAUKSEN

    if (res.data.length > 0) {
      const place = res.data[0];
      const newLat = parseFloat(place.lat);
      const newLon = parseFloat(place.lon);
      const newLocationName = place.display_name.split(",")[0]; // N√§ytet√§√§n vain ensimm√§inen osa nimest√§
      console.log("üìç Uusi paikka l√∂ydetty:", place.display_name); // TULOSTAA L√ñYDETYN PAIKAN
      console.log("üåç P√§ivitet√§√§n koordinaatit:", newLat, newLon); // Tulostaa uudet koordinaatit
      
      setLatitude(newLat);
      setLongitude(newLon);
      setTimeout(() => {
        setLocationName(newLocationName);
      }, 50);
    
      await reverseGeocode(newLat, newLon)
      fetchWeatherData(newLat, newLon);
    } else {
      console.warn("‚ö†Ô∏è Paikkaa ei l√∂ydy!");
      setError("Paikkaa ei l√∂ydy");
    }
  } catch (error) {
    console.error("‚ùå Paikkahaun virhe:", error);
    setError("Paikan haku ep√§onnistui");
  }
};

const getUserLocation = () => {
  if (!navigator.geolocation) {
    setError("‚ùå Selaimesi ei tue GPS:√§√§");
    return;
  }

  console.log("üì° Pyydet√§√§n GPS-lupaa..."); // üî• TULOSTAA DEBUG-VIESTIN

  navigator.permissions
    .query({ name: "geolocation" })
    .then((result) => {
      console.log("üìú GPS-luvan tila:", result.state); // üî• TULOSTAA LUVAN TILAN

      if (result.state === "denied") {
        setError("‚ùå GPS on estetty. Salli selaimen asetuksista!");
        return;
      }

      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const newLat = position.coords.latitude;
          const newLon = position.coords.longitude;

          console.log("üìç GPS-sijainti saatu:", newLat, newLon); // üî• Varmistus ett√§ GPS toimii

          setLatitude(newLat);
          setLongitude(newLon);
          setLocationName("Oma sijainti");
          await reverseGeocode(newLat, newLon);
          fetchWeatherData(newLat, newLon);
        },
        (error) => {
          console.error("‚ùå GPS-virhe:", error);
          setError("üìç GPS ei k√§ytett√§viss√§. Tarkista asetukset.");
        },
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
        
      );
    });
};

  const calculateMoonPhase = () => {
    const today = new Date();
    const lastNewMoon = new Date("2025-02-28");
    const msPerDay = 1000 * 60 * 60 * 24;
    const moonCycle = 29.53;
    let diffDays = Math.floor((today - lastNewMoon) / msPerDay) % moonCycle;
    if (diffDays < 0) diffDays += moonCycle;
    let phaseText = "üåë Uusikuu";
    if (diffDays < 1.5) phaseText = "üåë Uusikuu";
    else if (diffDays < 7.5) phaseText = "üåí Kasvava sirppi 1/4";
    else if (diffDays < 14.5) phaseText = "üåì Kasvava puolikuu 2/4";
    else if (diffDays < 16.5) phaseText = "üåï T√§ysikuu";
    else if (diffDays < 23) phaseText = "üåñ Pienenev√§ kuu 3/4";
    else phaseText = "üåó Pienenev√§ kuu 4/4";
    setMoonPhase(phaseText);
  };

  const fetchWeatherData = async (lat, lon) => {
  if (!lat || !lon) {
    console.error("‚ùå Koordinaatit puuttuvat, ei voida hakea s√§√§tietoja!");
    return;
  }
  setLoading(true);
  setError(null);
  try {
    console.log("üì° Haetaan s√§√§tiedot:", lat, lon); // Tulostaa haettavat koordinaatit
    const url = `https://opendata.fmi.fi/wfs?service=WFS&version=2.0.0&request=getFeature&storedquery_id=fmi::forecast::harmonie::surface::point::simple&latlon=${lat},${lon}&parameters=Temperature,FeelsLike,WindSpeedMS,WindDirection,Pressure,WeatherSymbol3`;
    const response = await axios.get(url);
    const parser = new XMLParser({ ignoreAttributes: false });
    const parsedResult = parser.parse(response.data);
    
    if (!parsedResult?.["wfs:FeatureCollection"]?.["wfs:member"]) {
      throw new Error("Virheellinen XML-muoto");
    }
    console.log("‚úÖ S√§√§data haettu onnistuneesti!");
    const elements = parsedResult["wfs:FeatureCollection"]["wfs:member"];
    const dataMap = {};
    
    elements.forEach((el) => {
      const element = el["BsWfs:BsWfsElement"];
      const time = new Date(element["BsWfs:Time"]);
      let hourKey = time.getHours();
      if (hourKey === 0) hourKey = 24; // Muunnetaan klo 00 ‚Üí 24
      
      const dayKey = time.toISOString().split("T")[0];
      const paramName = element["BsWfs:ParameterName"];
      let value = element["BsWfs:ParameterValue"];
      
      const openMap = () => {
        if (!latitude || !longitude) {
        setError("üìç Ei GPS-sijaintia. K√§yt√§ 'Oma Paikka' ensin!");
        <button onClick={openMap}>üìç Avaa kartta</button>
        return;
  }

      const url = `https://www.google.com/maps?q=${latitude},${longitude}`;
      console.log("üó∫Ô∏è Avataan kartta:", url);
      setTimeout(() => {
    window.open(url, "_blank", "noopener,noreferrer");
  }, 300);
};
      

      if ([8, 16, 24].includes(hourKey)) { // Nyt mukana my√∂s klo 24
        if (!dataMap[dayKey]) dataMap[dayKey] = {};
        if (!dataMap[dayKey][hourKey]) dataMap[dayKey][hourKey] = {};
        if (paramName === "FeelsLike" || paramName === "Temperature") {
          value = parseFloat(value).toFixed(1);
        }
        dataMap[dayKey][hourKey][paramName] = value;
      }
    });
    console.log("üå¶ P√§ivitetty s√§√§data:", dataMap); // Tulostaa uuden s√§√§datan
    setForecastData(dataMap);

  } catch (error) {
    console.error("‚ùå S√§√§tiedon haku ep√§onnistui:", error);
    setError("S√§√§tiedon haku ep√§onnistui");
  } finally {
    setLoading(false);
  }
};

  return (
    <div key={`${locationName}-${latitude}-${longitude}`}>
      <h2>üå¶ S√§√§ennuste</h2>
      <p>üìç {locationName} ({latitude || "-"}, {longitude || "-"})</p>
      <p>üåô Kuun vaihe: {moonPhase}</p>
      <input
        type="text"
        value={searchLocation}
        onChange={(e) => setSearchLocation(e.target.value)}
        placeholder="Hae paikka..."
      />
      <button onClick={searchForLocation}>üîç HAE</button>    
      <button onClick={getUserLocation}>OMA PAIKKA</button>
      {loading && <p>‚è≥ Ladataan s√§√§tietoja...</p>}
      {error && <p style={{ color: "red" }}>‚ö†Ô∏è {error}</p>}
      <div>
        {Object.entries(forecastData).map(([date, hours]) => (
          <div key={date}>
            <h3>{date}</h3>
            {Object.entries(hours).map(([hour, data]) => (
              <p key={hour}>
                ‚è∞ {hour}:00 üå° {data.Temperature}¬∞C (Tuntuu kuin {data.FeelsLike}¬∞C) üí® {data.WindSpeedMS}m/s {data.WindDirection}¬∞ üå§ S√§√§: {weatherDescriptions[data.WeatherSymbol3] || "Tuntematon"}
              </p>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Weather;
